import React from 'react';
import SingleBook from '../SingleBook/SingleBook'; 
import { Row } from 'react-bootstrap';

export const books = [
  { id: 1, title: "Il Signore degli Anelli", author: "J.R.R. Tolkien", cover: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTr_RZUZDDRxD0Gg5z2OuoT9vGZ3AJDfxwCQ&s" },
  { id: 2, title: "Il Hobbit", author: "J.R.R. Tolkien", cover: "data:image/jpg;base64," },
  { id: 3, title: "1984", author: "George Orwell", cover: "https://www.lafeltrinelli.it/images/9788807903816_0_0_536_0_75.jpg" },
  { id: 4, title: "Fahrenheit 451", author: "Ray Bradbury", cover: "data:image/jpg;base64," },
  { id: 5, title: "Brave New World", author: "Aldous Huxley", cover: "data:image/jpg;base64," },
];

const AllTheBooks = () => {
  return (
    <div className='container'>
      <Row className='my-4'>
        {books.map((book) => (
          <div className='col-12 col-md-6 col-lg-4 d-flex justify-content-center align-items-center' key={book.id}>
            <SingleBook book={book} /> 
          </div>
        ))}
      </Row>
    </div>
  );
};

export default AllTheBooks;
